const e=JSON.parse('{"key":"v-0c868226","path":"/%E7%AE%97%E6%B3%95%E9%A2%98/%E8%B4%AA%E5%BF%83%E7%AE%97%E6%B3%95/1.%E8%B4%AA%E5%BF%83%E7%AE%97%E6%B3%95%E5%9F%BA%E7%A1%80.html","title":"贪心算法基础","lang":"zh-CN","frontmatter":{"title":"贪心算法基础","order":1,"category":["算法题","贪心算法"],"description":"贪心算法的思想 贪心的本质是选择每一阶段的局部最优，从而达到全局最优。 例如，有一堆钞票，你可以拿走十张，如果想达到最大的金额，你要怎么拿？ 指定每次拿最大的，最终结果就是拿走最大数额的钱。 贪心算法并不想回溯，有固定的套路，因此贪心算法实际上就是在模拟局部最优的过程，从而推出整体最优 贪心算法的集体步骤 贪心算法一般分为如下四步： 将问题分解为若干个子问题 找出适合的贪心策略 求解每一个子问题的最优解 将局部最优解堆叠成全局最优解","head":[["meta",{"property":"og:url","content":"https://mister-hope.github.io/%E7%AE%97%E6%B3%95%E9%A2%98/%E8%B4%AA%E5%BF%83%E7%AE%97%E6%B3%95/1.%E8%B4%AA%E5%BF%83%E7%AE%97%E6%B3%95%E5%9F%BA%E7%A1%80.html"}],["meta",{"property":"og:site_name","content":"博客演示"}],["meta",{"property":"og:title","content":"贪心算法基础"}],["meta",{"property":"og:description","content":"贪心算法的思想 贪心的本质是选择每一阶段的局部最优，从而达到全局最优。 例如，有一堆钞票，你可以拿走十张，如果想达到最大的金额，你要怎么拿？ 指定每次拿最大的，最终结果就是拿走最大数额的钱。 贪心算法并不想回溯，有固定的套路，因此贪心算法实际上就是在模拟局部最优的过程，从而推出整体最优 贪心算法的集体步骤 贪心算法一般分为如下四步： 将问题分解为若干个子问题 找出适合的贪心策略 求解每一个子问题的最优解 将局部最优解堆叠成全局最优解"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2024-01-21T08:05:49.000Z"}],["meta",{"property":"article:author","content":"TX"}],["meta",{"property":"article:modified_time","content":"2024-01-21T08:05:49.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"贪心算法基础\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2024-01-21T08:05:49.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"TX\\",\\"url\\":\\"https://mister-hope.com\\"}]}"]]},"headers":[{"level":2,"title":"贪心算法的思想","slug":"贪心算法的思想","link":"#贪心算法的思想","children":[]},{"level":2,"title":"贪心算法的集体步骤","slug":"贪心算法的集体步骤","link":"#贪心算法的集体步骤","children":[]}],"git":{"createdTime":1705824349000,"updatedTime":1705824349000,"contributors":[{"name":"RezeLove","email":"1224745953@qq.com","commits":1}]},"readingTime":{"minutes":0.71,"words":212},"filePathRelative":"算法题/贪心算法/1.贪心算法基础.md","localizedDate":"2024年1月21日","excerpt":"<h2> 贪心算法的思想</h2>\\n<p>贪心的本质是选择每一阶段的局部最优，从而达到全局最优。</p>\\n<p>例如，有一堆钞票，你可以拿走十张，如果想达到最大的金额，你要怎么拿？</p>\\n<p>指定每次拿最大的，最终结果就是拿走最大数额的钱。</p>\\n<p>贪心算法并不想回溯，有固定的套路，因此贪心算法实际上就是在模拟局部最优的过程，从而推出整体最优</p>\\n<h2> 贪心算法的集体步骤</h2>\\n<p>贪心算法一般分为如下四步：</p>\\n<ul>\\n<li>将问题分解为若干个子问题</li>\\n<li>找出适合的贪心策略</li>\\n<li>求解每一个子问题的最优解</li>\\n<li>将局部最优解堆叠成全局最优解</li>\\n</ul>","autoDesc":true}');export{e as data};
